";    var data = {      "id":id    };    $.post(url,data,function(data){      if(data.status == "success"){      }else{        alert("删除失败");      }    },"JSON");  }}</script><iframe name="download" style="display:none"></iframe>    <!-- /#wrapper -->    <!-- jQuery -->    <script src="../plugins/bower_components/jquery/dist/jquery.min.js"></script>    <!-- Bootstrap Core JavaScript -->    <script src="bootstrap/dist/js/bootstrap.min.js"></script>    <!-- Menu Plugin JavaScript -->    <script src="../plugins/bower_components/sidebar-nav/dist/sidebar-nav.min.js"></script>    <!--slimscroll JavaScript -->    <script src="js/jquery.slimscroll.js"></script>    <!--Wave Effects -->    <script src="js/waves.js"></script>    <!-- Custom Theme JavaScript -->    <script src="js/custom.min.js"></script>    <!-- jQuery peity -->    <script src="../plugins/bower_components/peity/jquery.peity.min.js"></script>    <script src="../plugins/bower_components/peity/jquery.peity.init.js"></script>    <!--Style Switcher -->    <script src="../plugins/bower_components/styleswitcher/jQuery.style.switcher.js"></script>    <!--Style Switcher --><script src="../plugins/bower_components/styleswitcher/jQuery.style.switcher.js"></script></body></html>